import React from 'react';
import { Modal, Button } from 'react-bootstrap';
import './MessageUI.css';

const MessageUI = ({ show, onClose, message, type }) => {
  // type: 'error' or 'success'
  return (
    <Modal show={show} onHide={onClose} centered>
      <Modal.Header closeButton className={`modal-header-${type}`}>
        <Modal.Title>{type === 'error' ? 'Error' : 'Success'}</Modal.Title>
      </Modal.Header>
      <Modal.Body className={`modal-body-${type}`}>
        <p>{message}</p>
      </Modal.Body>
      <Modal.Footer>
        <Button variant={type === 'error' ? 'danger' : 'success'} onClick={onClose}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default MessageUI;
