import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Card, Row, Col } from 'react-bootstrap';
import { Line, Bar } from 'react-chartjs-2';
import 'chart.js/auto';

function Dashboard() {
  const [analytics, setAnalytics] = useState({
    salesTrend: {},
    inventoryTurnover: {},
    supplierPerformance: {}
  });

  useEffect(() => {
    // Fetch analytics data from backend endpoints
    // (This is a placeholder – implement actual endpoints and logic)
    axios.get('http://localhost:5001/api/analytics').then((res) => {
      setAnalytics(res.data);
    }).catch((err) => console.error(err));
  }, []);

  return (
    <div>
      <h2>Analytics Dashboard</h2>
      <Row className="mb-4">
        <Col md={6}>
          <Card>
            <Card.Header>Sales Trend</Card.Header>
            <Card.Body>
              <Line data={analytics.salesTrend} />
            </Card.Body>
          </Card>
        </Col>
        <Col md={6}>
          <Card>
            <Card.Header>Inventory Turnover</Card.Header>
            <Card.Body>
              <Bar data={analytics.inventoryTurnover} />
            </Card.Body>
          </Card>
        </Col>
      </Row>
      <Row>
        <Col>
          <Card>
            <Card.Header>Supplier Performance</Card.Header>
            <Card.Body>
              <Bar data={analytics.supplierPerformance} />
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default Dashboard;
