// src/components/AuthLogin_Register/AuthPage.js
import React, { useState } from 'react';
import axios from 'axios';
import { Container, Row, Col, Card, Form, Button, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import './AuthPage.css';

function AuthPage({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Form fields
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('owner');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        const res = await axios.post('http://localhost:5001/api/auth/login', {
          username,
          password,
        });
        const token = res.data.accessToken;
        onLogin(token);
      } else {
        await axios.post('http://localhost:5001/api/auth/signup', {
          username,
          password,
          role,
        });
        // Switch to login mode after successful registration.
        setIsLogin(true);
        setError('');
        setUsername('');
        setPassword('');
        setRole('owner');
      }
    } catch (err) {
      setError(
        err.response?.data?.error ||
          (isLogin
            ? 'Login failed. Please check your credentials.'
            : 'Registration failed. Please try again.')
      );
    }
  };

  return (
    <div className="auth-page">
      <Container fluid>
        <Row className="min-vh-100">
          {/* Left Side: Background image and tagline */}
          <Col md={6} className="left-side d-none d-md-flex align-items-center justify-content-center">
            <div className="left-content text-center">
              <h1>Welcome to Smart Inventory</h1>
              <p>Efficiently manage your stock, orders, and suppliers with ease.</p>
            </div>
          </Col>
          {/* Right Side: Form */}
          <Col xs={12} md={6} className="right-side d-flex align-items-center justify-content-center">
            <Card className="auth-card shadow">
              <Card.Body>
                <h2 className="text-center mb-3">Smart Inventory</h2>
                <h4 className="text-center mb-4">{isLogin ? 'Login' : 'Register'}</h4>
                {error && <Alert variant="danger">{error}</Alert>}
                <Form onSubmit={handleSubmit}>
                  <Form.Group className="mb-3" controlId="formUsername">
                    <Form.Label>Username</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Enter username"
                      value={username}
                      onChange={(e) => setUsername(e.target.value)}
                      required
                    />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="formPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                      type="password"
                      placeholder="Enter password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      required
                    />
                  </Form.Group>
                  {!isLogin && (
                    <Form.Group className="mb-3" controlId="formRole">
                      <Form.Label>Role</Form.Label>
                      <Form.Select value={role} onChange={(e) => setRole(e.target.value)}>
                        <option value="owner">Owner</option>
                        <option value="supplier">Supplier</option>
                      </Form.Select>
                    </Form.Group>
                  )}
                  <Button variant="primary" type="submit" className="w-100">
                    {isLogin ? 'Login' : 'Register'}
                  </Button>
                </Form>
                <div className="text-center mt-3">
                  {isLogin ? (
                    <>
                      Donâ€™t have an account?{' '}
                      <Button
                        variant="link"
                        onClick={() => {
                          setIsLogin(false);
                          setError('');
                        }}
                      >
                        Register here
                      </Button>
                    </>
                  ) : (
                    <>
                      Already have an account?{' '}
                      <Button
                        variant="link"
                        onClick={() => {
                          setIsLogin(true);
                          setError('');
                        }}
                      >
                        Login here
                      </Button>
                    </>
                  )}
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default AuthPage;
