// LinkedInTest.js
import React, { useEffect } from 'react';
import { Button } from 'react-bootstrap';
import { LinkedIn } from 'react-linkedin-login-oauth2';

const LinkedInTest = () => {
  // Log when the component mounts and unmounts
  useEffect(() => {
    console.log('[DEBUG] LinkedInTest component mounted');
    return () => {
      console.log('[DEBUG] LinkedInTest component unmounted');
    };
  }, []);

  // Test for a standalone button
  const handleTestClick = () => {
    console.log('[DEBUG] Test button clicked!');
    alert('Test button clicked!');
  };

  // Handlers for the LinkedIn component
  const handleLinkedInSuccess = (data) => {
    console.log('[DEBUG] LinkedIn success:', data);
    alert('LinkedIn login success, check console for details.');
  };

  const handleLinkedInError = (error) => {
    console.error('[DEBUG] LinkedIn error:', error);
    alert('LinkedIn login error, check console for details.');
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>LinkedIn Button Test</h2>
      {/* Standalone test button */}
      <Button 
        variant="primary" 
        onClick={handleTestClick} 
        style={{ marginBottom: '20px' }}
      >
        Test Simple Button
      </Button>
      <br />
      {/* LinkedIn OAuth Button */}
      <LinkedIn
        clientId="86bco2ofgo5q6u"
        redirectUri={`${window.location.origin}/linkedin`}
        onSuccess={handleLinkedInSuccess}
        onError={handleLinkedInError}
      >
        {({ linkedInLogin, disabled }) => (
          <Button
            variant="outline-info"
            // Add a ref callback to log the DOM node for debugging.
            ref={(node) => {
              if (node) {
                console.log('[DEBUG] LinkedIn Login Button DOM node:', node);
              } else {
                console.log('[DEBUG] LinkedIn Login Button ref is null');
              }
            }}
            onClick={() => {
              console.log('[DEBUG] LinkedIn button onClick triggered');
              // Log the linkedInLogin function to verify it's valid
              console.log('[DEBUG] linkedInLogin function:', linkedInLogin);
              if (typeof linkedInLogin === 'function') {
                linkedInLogin();
              } else {
                console.error('[DEBUG] linkedInLogin is not a function');
              }
            }}
            disabled={disabled}
          >
            LinkedIn Login
          </Button>
        )}
      </LinkedIn>
    </div>
  );
};

export default LinkedInTest;
