// frontend/src/components/LoginPage.js
import React, { useState } from 'react';
import axios from 'axios';
import { Button, Form, Alert, Container, Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './LoginPage.css'; // Custom CSS file for this page

function LoginPage({ onLogin }) {
  const [activeTab, setActiveTab] = useState('owner'); 
  const [form, setForm] = useState({ username: '', password: '' });
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5001/api/auth/login', form);
      const token = res.data.accessToken;
      onLogin(token);
    } catch (err) {
      setError('Invalid credentials. Please try again.');
    }
  };

  return (
    <div className="login-page-container">
      <Container fluid>
        <Row>
          {/* Left side - illustration or color block */}
          <Col md={6} className="login-illustration d-none d-md-block">
            {/* You could place an actual image here if you like */}
            <div className="login-illustration-content">
              <h1>Smart Inventory</h1>
              <p>Manage your stock, orders, and suppliers with ease.</p>
            </div>
          </Col>

          {/* Right side - form */}
          <Col md={6} className="login-form-col">
            <div className="login-form-wrapper">
              <h2 className="text-center mb-4">
                {activeTab === 'owner' ? 'Owner Login' : 'Supplier Login'}
              </h2>
              <div className="d-flex justify-content-center mb-3">
                <Button
                  variant={activeTab === 'owner' ? 'primary' : 'outline-primary'}
                  onClick={() => setActiveTab('owner')}
                  className="me-2"
                >
                  Owner
                </Button>
                <Button
                  variant={activeTab === 'supplier' ? 'primary' : 'outline-primary'}
                  onClick={() => setActiveTab('supplier')}
                >
                  Supplier
                </Button>
              </div>
              {error && <Alert variant="danger">{error}</Alert>}
              <Form onSubmit={handleSubmit}>
                <Form.Group controlId="username" className="mb-3">
                  <Form.Label>Username</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Enter username"
                    value={form.username}
                    onChange={(e) => setForm({ ...form, username: e.target.value })}
                    required
                  />
                </Form.Group>
                <Form.Group controlId="password" className="mb-3">
                  <Form.Label>Password</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="Enter password"
                    value={form.password}
                    onChange={(e) => setForm({ ...form, password: e.target.value })}
                    required
                  />
                </Form.Group>
                <Button variant="success" type="submit" className="w-100 mb-3">
                  Login
                </Button>
              </Form>
              <div className="text-center">
                Donâ€™t have an account? <Link to="/signup">Sign up here</Link>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default LoginPage;
