// src/components/AuthLogin_Register/AuthPage.js
import React, { useState } from 'react';
import axios from 'axios';
import { Card, Form, Button, Alert } from 'react-bootstrap';
import { GoogleLogin } from '@react-oauth/google';
import { LinkedIn } from 'react-linkedin-login-oauth2';
import { FaGoogle, FaLinkedinIn } from 'react-icons/fa';
import './AuthPage.css';

function AuthPage({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [error, setError] = useState('');

  // Common fields
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // Registration-only fields (if you later add registration)
  const [contactType, setContactType] = useState('email'); // 'email' or 'phone'
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [role, setRole] = useState('owner');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        const res = await axios.post('http://localhost:5001/api/auth/login', {
          username,
          password,
        });
        const token = res.data.accessToken;
        onLogin(token);
      } else {
        const registrationData = { username, password, role };
        if (contactType === 'email') {
          registrationData.email = email;
        } else {
          registrationData.phone = phone;
        }
        await axios.post('http://localhost:5001/api/auth/signup', registrationData);
        setIsLogin(true);
        setError('');
        setUsername('');
        setPassword('');
        setEmail('');
        setPhone('');
        setRole('owner');
      }
    } catch (err) {
      setError(
        err.response?.data?.error ||
          (isLogin
            ? 'Login failed. Please check your credentials.'
            : 'Registration failed. Please try again.')
      );
    }
  };

  const handleSocialLogin = async (provider, response) => {
    // Stub for OAuth login; replace with your own flow.
    const dummyToken = `dummy-${provider}-token`;
    onLogin(dummyToken);
  };

  return (
    <div className="auth-page">
      <div className="left-side">
        <div className="left-content">
          <h1>Welcome to Smart Inventory</h1>
          <p>Efficiently manage your stock, orders, and suppliers with ease.</p>
        </div>
      </div>
      <div className="right-side">
        <Card className="auth-card shadow">
          <Card.Body>
            <h2 className="text-center mb-3">Smart Inventory</h2>
            <h4 className="text-center mb-4">{isLogin ? 'Login' : 'Register'}</h4>
            {error && <Alert variant="danger">{error}</Alert>}
            <Form onSubmit={handleSubmit}>
              <Form.Group className="mb-3" controlId="formUsername">
                <Form.Label>Username</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </Form.Group>
              <Form.Group className="mb-3" controlId="formPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Enter password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </Form.Group>
              <Button variant="primary" type="submit" className="w-100 mb-3">
                {isLogin ? 'Login' : 'Register'}
              </Button>
            </Form>
            <div className="social-login text-center my-3">
              <p>Or continue with</p>
              <div className="social-buttons d-flex justify-content-center gap-3">
                <div className="social-button">
                  <FaGoogle size={24} style={{ marginRight: '8px' }} />
                  <GoogleLogin
                    onSuccess={(credentialResponse) =>
                      handleSocialLogin('google', credentialResponse)
                    }
                    onError={() => {}}
                  />
                </div>
                <div className="social-button">
                  <LinkedIn
                    clientId="86bco2ofgo5q6u"
                    redirectUri={`${window.location.origin}/linkedin`}
                    onSuccess={(code) => handleSocialLogin('linkedin', code)}
                    onError={() => {}}
                    scope="openid profile email"
                  >
                    {({ linkedInLogin, disabled }) => (
                      <Button
                        variant="outline-info"
                        onClick={() => {
                          if (typeof linkedInLogin === 'function') linkedInLogin();
                        }}
                        disabled={disabled}
                      >
                        <FaLinkedinIn />
                      </Button>
                    )}
                  </LinkedIn>
                </div>
              </div>
            </div>
            <div className="text-center mt-3">
              {isLogin ? (
                <>
                  Don’t have an account?{' '}
                  <Button variant="link" onClick={() => { setIsLogin(false); setError(''); }}>
                    Register here
                  </Button>
                </>
              ) : (
                <>
                  Already have an account?{' '}
                  <Button variant="link" onClick={() => { setIsLogin(true); setError(''); }}>
                    Login here
                  </Button>
                </>
              )}
            </div>
          </Card.Body>
        </Card>
      </div>
    </div>
  );
}

export default AuthPage;
