// frontend/src/components/LoginPage.js
import React, { useState } from 'react';
import axios from 'axios';
import { Button, Form, Alert, Container, Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';

function LoginPage({ onLogin }) {
  const [activeTab, setActiveTab] = useState('owner'); // "owner" or "supplier"
  const [form, setForm] = useState({ username: '', password: '' });
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5001/api/auth/login', form);
      const token = res.data.accessToken;
      onLogin(token);
    } catch (err) {
      setError('Invalid credentials. Please try again.');
    }
  };

  return (
    <Container className="mt-5">
      <Row className="justify-content-md-center">
        <Col md={6}>
          <h2 className="text-center">
            {activeTab === 'owner' ? 'Owner Login' : 'Supplier Login'}
          </h2>
          <div className="d-flex justify-content-center mb-3">
            <Button 
              variant={activeTab === 'owner' ? 'primary' : 'secondary'} 
              onClick={() => setActiveTab('owner')}
              className="me-2"
            >
              Owner
            </Button>
            <Button 
              variant={activeTab === 'supplier' ? 'primary' : 'secondary'} 
              onClick={() => setActiveTab('supplier')}
            >
              Supplier
            </Button>
          </div>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="username" className="mb-3">
              <Form.Label>Username</Form.Label>
              <Form.Control 
                type="text" 
                placeholder="Enter username" 
                value={form.username}
                onChange={(e) => setForm({ ...form, username: e.target.value })}
                required 
              />
            </Form.Group>
            <Form.Group controlId="password" className="mb-3">
              <Form.Label>Password</Form.Label>
              <Form.Control 
                type="password" 
                placeholder="Enter password" 
                value={form.password}
                onChange={(e) => setForm({ ...form, password: e.target.value })}
                required 
              />
            </Form.Group>
            <Button variant="primary" type="submit" className="w-100">
              Login as {activeTab === 'owner' ? 'Owner' : 'Supplier'}
            </Button>
          </Form>
          <div className="text-center mt-3">
            Don't have an account? <Link to="/signup">Sign up here</Link>
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default LoginPage;
