// src/components/AuthLogin_Register/AuthPage.js
import React, { useState } from 'react';
import axios from 'axios';
import { Container, Row, Col, Card, Form, Button, Alert } from 'react-bootstrap';
import { GoogleLogin } from '@react-oauth/google';
import { LinkedIn } from 'react-linkedin-login-oauth2';
import { FaGoogle, FaLinkedinIn } from 'react-icons/fa';
import './AuthPage.css';

function AuthPage({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [error, setError] = useState('');

  // Common fields
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // Registration-only fields
  const [contactType, setContactType] = useState('email'); // 'email' or 'phone'
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [role, setRole] = useState('owner');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        const res = await axios.post('http://localhost:5001/api/auth/login', {
          username,
          password,
        });
        const token = res.data.accessToken;
        // Pass the token to parent or store it (e.g., localStorage)
        onLogin(token);
      } else {
        // Registration
        const registrationData = { username, password, role };
        if (contactType === 'email') {
          registrationData.email = email;
        } else {
          registrationData.phone = phone;
        }
        await axios.post('http://localhost:5001/api/auth/signup', registrationData);

        // Switch to login mode
        setIsLogin(true);
        setError('');
        setUsername('');
        setPassword('');
        setEmail('');
        setPhone('');
        setRole('owner');
      }
    } catch (err) {
      setError(
        err.response?.data?.error ||
          (isLogin
            ? 'Login failed. Please check your credentials.'
            : 'Registration failed. Please try again.')
      );
    }
  };

  /**
   * Handle social logins (Google, LinkedIn, etc.).
   * @param {string} provider - "google" or "linkedin"
   * @param {object} data - Response data from the provider
   */
  const handleSocialLogin = async (provider, data) => {
    try {
      if (provider === 'google') {
        // data contains Google Credential Response
        const { credential } = data || {};

        if (!credential) {
          setError('Google login failed: No credential received.');
          return;
        }

        // Send the credential (ID token) to the backend for verification/exchange
        const res = await axios.post('http://localhost:5001/api/auth/google', {
          id_token: credential
        });

        // Assuming your backend returns a JWT or session token
        const token = res.data.token;
        onLogin(token);

      } else if (provider === 'linkedin') {
        // data should be the LinkedIn "code"
        const { code } = data;

        if (!code) {
          setError('LinkedIn login failed: No code received.');
          return;
        }

        // Exchange authorization code for an access token (via your backend)
        const res = await axios.post('http://localhost:5001/api/auth/linkedin', {
          code
        });
        const token = res.data.token;
        onLogin(token);
      }
    } catch (err) {
      console.error(`${provider} login error:`, err);
      setError(`${provider} login failed. Please try again.`);
    }
  };

  return (
    <div className="auth-page">
      <Container fluid>
        <Row className="min-vh-100">
          {/* Left Side */}
          <Col md={6} className="left-side d-flex align-items-center justify-content-center">
            <div className="left-content text-center">
              <h1>Welcome to Smart Inventory</h1>
              <p>Efficiently manage your stock, orders, and suppliers with ease.</p>
            </div>
          </Col>

          {/* Right Side: Form */}
          <Col xs={12} md={6} className="right-side d-flex align-items-center justify-content-center">
            <Card className={`auth-card shadow ${!isLogin ? 'wider-card' : ''}`}>
              <Card.Body>
                <h2 className="text-center mb-3">Smart Inventory</h2>
                <h4 className="text-center mb-4">{isLogin ? 'Login' : 'Register'}</h4>
                {error && <Alert variant="danger">{error}</Alert>}

                <Form onSubmit={handleSubmit}>
                  <Form.Group className="mb-3" controlId="formUsername">
                    <Form.Label>Username</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Enter username"
                      value={username}
                      onChange={(e) => setUsername(e.target.value)}
                      required
                    />
                  </Form.Group>

                  {!isLogin && (
                    <>
                      <Form.Group className="mb-3" controlId="contactTypeToggle">
                        <Form.Label>Register With</Form.Label>
                        <div>
                          <Button
                            variant={contactType === 'email' ? 'primary' : 'outline-primary'}
                            onClick={() => setContactType('email')}
                            className="me-2"
                          >
                            Email
                          </Button>
                          <Button
                            variant={contactType === 'phone' ? 'primary' : 'outline-primary'}
                            onClick={() => setContactType('phone')}
                          >
                            Phone
                          </Button>
                        </div>
                      </Form.Group>

                      {contactType === 'email' ? (
                        <Form.Group className="mb-3" controlId="formEmail">
                          <Form.Label>Email</Form.Label>
                          <Form.Control
                            type="email"
                            placeholder="Enter email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                          />
                        </Form.Group>
                      ) : (
                        <Form.Group className="mb-3" controlId="formPhone">
                          <Form.Label>Phone Number</Form.Label>
                          <Form.Control
                            type="tel"
                            placeholder="Enter phone number"
                            value={phone}
                            onChange={(e) => setPhone(e.target.value)}
                            required
                          />
                        </Form.Group>
                      )}
                    </>
                  )}

                  <Form.Group className="mb-3" controlId="formPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                      type="password"
                      placeholder="Enter password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      required
                    />
                  </Form.Group>

                  {!isLogin && (
                    <Form.Group className="mb-3" controlId="formRole">
                      <Form.Label>Role</Form.Label>
                      <Form.Select value={role} onChange={(e) => setRole(e.target.value)}>
                        <option value="owner">Owner</option>
                        <option value="supplier">Supplier</option>
                      </Form.Select>
                    </Form.Group>
                  )}

                  <Button variant="primary" type="submit" className="w-100 mb-3">
                    {isLogin ? 'Login' : 'Register'}
                  </Button>
                </Form>

                {/* Social Login */}
                <div className="social-login text-center my-3">
                  <p>Or continue with</p>
                  <div className="social-buttons d-flex justify-content-center gap-3">
                    {/* Google OAuth */}
                    <div className="social-button d-flex flex-column align-items-center">
                      <FaGoogle size={24} style={{ marginBottom: '8px' }} />
                      <GoogleLogin
                        onSuccess={(credentialResponse) =>
                          handleSocialLogin('google', credentialResponse)
                        }
                        onError={() => console.log('Google login failed')}
                      />
                    </div>

                    {/* LinkedIn OAuth */}
                    <div className="social-button d-flex flex-column align-items-center">
                      <FaLinkedinIn size={24} style={{ marginBottom: '8px' }} />
                      <LinkedIn
                        clientId="86bco2ofgo5q6u"
                        redirectUri={`${window.location.origin}/linkedin`}
                        onSuccess={(code) => handleSocialLogin('linkedin', { code })}
                        onError={(error) => {
                          console.log('LinkedIn Error:', error);
                          setError('LinkedIn login failed. Please try again.');
                        }}
                      >
                        {({ onClick, disabled }) => (
                          <Button variant="outline-info" onClick={onClick} disabled={disabled}>
                            Sign in with LinkedIn
                          </Button>
                        )}
                      </LinkedIn>
                    </div>
                  </div>
                </div>

                <div className="text-center mt-3">
                  {isLogin ? (
                    <>
                      Don’t have an account?{' '}
                      <Button
                        variant="link"
                        onClick={() => {
                          setIsLogin(false);
                          setError('');
                        }}
                      >
                        Register here
                      </Button>
                    </>
                  ) : (
                    <>
                      Already have an account?{' '}
                      <Button
                        variant="link"
                        onClick={() => {
                          setIsLogin(true);
                          setError('');
                        }}
                      >
                        Login here
                      </Button>
                    </>
                  )}
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default AuthPage;
