// src/components/AuthLogin_Register/AuthPage.js

import React, { useState } from 'react';
import axios from 'axios';
import { Card, Form, Button, Alert } from 'react-bootstrap';
import { useGoogleLogin } from '@react-oauth/google';
import { LinkedIn } from 'react-linkedin-login-oauth2';
import './AuthPage.css';

function AuthPage({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [fade, setFade] = useState(true);
  const [error, setError] = useState('');

  // Common fields
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // Registration-only fields
  const [email, setEmail] = useState('');
  const [role, setRole] = useState('');
  const [showOptions, setShowOptions] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        const res = await axios.post('http://localhost:5001/api/auth/login', { username, password });
        const token = res.data.accessToken;
        onLogin(token);
      } else {
        // Now email is positioned between username and password, and role is set via the custom dropdown.
        const registrationData = { username, email, password, role };
        await axios.post('http://localhost:5001/api/auth/signup', registrationData);
        // Switch back to login mode after successful registration
        toggleMode();
        setUsername('');
        setPassword('');
        setEmail('');
        setRole('');
      }
    } catch (err) {
      setError(
        err.response?.data?.error ||
          (isLogin
            ? 'Login failed. Please check your credentials.'
            : 'Registration failed. Please try again.')
      );
    }
  };

  const handleSocialLogin = async (provider, response) => {
    // Replace with your actual OAuth flow.
    const dummyToken = `dummy-${provider}-token`;
    onLogin(dummyToken);
  };

  const googleLogin = useGoogleLogin({
    onSuccess: (credentialResponse) => handleSocialLogin('google', credentialResponse),
    onError: () => {},
  });

  // Toggle mode with a fade transition
  const toggleMode = () => {
    setFade(false);
    setTimeout(() => {
      setIsLogin((prev) => !prev);
      setError('');
      setFade(true);
    }, 300);
  };

  return (
    <div className="auth-page">
      {/* Left Side */}
      <div className="left-side">
        <div className="left-content">
          <h1>Welcome</h1>
          <p>Manage your inventory with ease.</p>
        </div>
      </div>

      {/* Right Side */}
      <div className="right-side">
        <Card className={`auth-card ${!isLogin ? 'register-mode' : ''}`}>
          <Card.Body className={fade ? 'fade-in' : 'fade-out'}>
            <h4>{isLogin ? 'Login' : 'Register'}</h4>
            {error && <Alert variant="danger">{error}</Alert>}
            <Form onSubmit={handleSubmit}>
              {/* Username Field */}
              <Form.Group className="mb-3" controlId="formUsername">
                <Form.Label className="form-label">Username</Form.Label>
                <Form.Control
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </Form.Group>

              {/* Registration mode: Email now positioned between username and password */}
              {!isLogin && (
                <Form.Group className="mb-3" controlId="formEmail">
                  <Form.Label className="form-label">Email</Form.Label>
                  <Form.Control
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </Form.Group>
              )}

              {/* Password Field */}
              <Form.Group className="mb-3" controlId="formPassword">
                <Form.Label className="form-label">Password</Form.Label>
                <Form.Control
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </Form.Group>

              {/* Registration mode: Custom Role Dropdown */}
              {!isLogin && (
                <div className="mb-3">
                  <label className="form-label">Role</label>
                  <div className="select-menu">
                    <div className="select-btn" onClick={() => setShowOptions(!showOptions)}>
                      <span className="sBtn-text">{role || "Select your role"}</span>
                      <i className="bx bx-chevron-down"></i>
                    </div>
                    {showOptions && (
                      <ul className="options">
                        <li className="option" onClick={() => { setRole("supplier"); setShowOptions(false); }}>
                          <i className="bx bxl-github"></i>
                          <span className="option-text">Supplier</span>
                        </li>
                        <li className="option" onClick={() => { setRole("owner"); setShowOptions(false); }}>
                          <i className="bx bxl-github"></i>
                          <span className="option-text">Owner</span>
                        </li>
                        <li className="option" onClick={() => { setRole("employee"); setShowOptions(false); }}>
                          <i className="bx bxl-github"></i>
                          <span className="option-text">Employee</span>
                        </li>
                      </ul>
                    )}
                  </div>
                </div>
              )}

              <Button variant="primary" type="submit" className="mb-3">
                {isLogin ? 'Login' : 'Register'}
              </Button>
            </Form>
            <div className="divider">
              <span>{isLogin ? 'or login with' : 'or register with'}</span>
            </div>
            <div className="social-buttons">
              <div className="social-button">
                <Button variant="outline-info" className="google-btn" onClick={googleLogin}>
                  <img src="https://developers.google.com/identity/images/g-logo.png" alt="Google" />
                  {isLogin ? 'Login with Google' : 'Register with Google'}
                </Button>
              </div>
              <div className="social-button">
                <LinkedIn
                  clientId="86bco2ofgo5q6u"
                  redirectUri={`${window.location.origin}/linkedin`}
                  onSuccess={(code) => handleSocialLogin('linkedin', code)}
                  onError={() => {}}
                  scope="openid profile email"
                >
                  {({ linkedInLogin, disabled }) => (
                    <Button
                      variant="outline-info"
                      className="linkedin-btn"
                      onClick={() => {
                        if (typeof linkedInLogin === 'function') linkedInLogin();
                      }}
                      disabled={disabled}
                    >
                      <img src="https://cdn-icons-png.flaticon.com/512/174/174857.png" alt="LinkedIn" />
                      {isLogin ? 'Login with LinkedIn' : 'Register with LinkedIn'}
                    </Button>
                  )}
                </LinkedIn>
              </div>
            </div>
            <div className="text-center mt-3 account-switch">
              {isLogin ? (
                <>
                  Don’t have an account?{' '}
                  <span
                    onClick={toggleMode}
                    style={{
                      cursor: 'pointer',
                      color: '#007bff',
                      textDecoration: 'underline',
                      background: 'none',
                      padding: 0,
                      border: 'none'
                    }}
                  >
                    Register here
                  </span>
                </>
              ) : (
                <>
                  Already have an account?{' '}
                  <span
                    onClick={toggleMode}
                    style={{
                      cursor: 'pointer',
                      color: '#007bff',
                      textDecoration: 'underline',
                      background: 'none',
                      padding: 0,
                      border: 'none'
                    }}
                  >
                    Login here
                  </span>
                </>
              )}
            </div>
          </Card.Body>
        </Card>
      </div>
    </div>
  );
}

export default AuthPage;
