// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/NavbarUI/Navbar';
import Sidebar from './components/Sidebar';
import InventoryDashboard from './components/InventoryDashboard';
import ProductList from './components/ProductList';
import SupplierList from './components/SupplierList';
import OrderList from './components/OrderList';
import WarehouseList from './components/WarehouseList';
import NotificationsPanel from './components/NotificationsPanel';
import ThemeToggle from './components/ThemeToggle';
import AuthPage from './components/AuthLogin_Register/AuthPage';
import LinkedInCallback from './components/AuthLogin_Register/LinkedInCallback';
import './App.css';

function App() {
  const [theme, setTheme] = useState('light');
  const [token, setToken] = useState(localStorage.getItem('token') || null);

  const handleLogin = (token) => {
    setToken(token);
    localStorage.setItem('token', token);
  };

  return (
    <Router>
      <Routes>
        {token ? (
          // Protected Routes (User is authenticated)
          <>
            <Route
              path="/*"
              element={
                <div className={`app ${theme}`}>
                  <Navbar />
                  <div className="d-flex">
                    <Sidebar />
                    <div className="content flex-grow-1 p-4">
                      <div className="d-flex justify-content-end mb-3">
                        <ThemeToggle theme={theme} setTheme={setTheme} />
                      </div>
                      <Routes>
                        <Route path="/" element={<InventoryDashboard />} />
                        <Route path="/products" element={<ProductList />} />
                        <Route path="/suppliers" element={<SupplierList />} />
                        <Route path="/orders" element={<OrderList />} />
                        <Route path="/warehouses" element={<WarehouseList />} />
                        <Route path="/notifications" element={<NotificationsPanel />} />
                      </Routes>
                    </div>
                  </div>
                </div>
              }
            />
          </>
        ) : (
          // Unauthenticated Routes (User is not logged in)
          <>
            {/* Route for handling LinkedIn OAuth callback */}
            <Route path="/linkedin" element={<LinkedInCallback onLogin={handleLogin} />} />
            {/* Explicit login route */}
            <Route path="/auth" element={<AuthPage onLogin={handleLogin} />} />
            {/* Fallback for all other paths */}
            <Route path="/*" element={<AuthPage onLogin={handleLogin} />} />
          </>
        )}
      </Routes>
    </Router>
  );
}

export default App;
