import React, { useState } from 'react';
import axios from 'axios';
import { Card, Form, Button, Alert } from 'react-bootstrap';
import { useGoogleLogin } from '@react-oauth/google';
import { LinkedIn } from 'react-linkedin-login-oauth2';
import './AuthPage.css';

function AuthPage({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [fade, setFade] = useState(true);
  const [error, setError] = useState('');

  // Common fields
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // Registration-only fields
  const [role, setRole] = useState(''); // Default is empty

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        const res = await axios.post('http://localhost:5001/api/auth/login', { username, password });
        const token = res.data.accessToken;
        onLogin(token);
      } else {
        const registrationData = { username, password, role };
        await axios.post('http://localhost:5001/api/auth/signup', registrationData);
        // Switch back to login mode after successful registration
        toggleMode();
        setUsername('');
        setPassword('');
        setRole('');
      }
    } catch (err) {
      setError(
        err.response?.data?.error ||
          (isLogin
            ? 'Login in failed. Please check your credentials.'
            : 'Registration failed. Please try again.')
      );
    }
  };

  const handleSocialLogin = async (provider, response) => {
    // Replace with your actual OAuth flow.
    const dummyToken = `dummy-${provider}-token`;
    onLogin(dummyToken);
  };

  const googleLogin = useGoogleLogin({
    onSuccess: (credentialResponse) => handleSocialLogin('google', credentialResponse),
    onError: () => {},
  });

  // Toggle mode with a fade transition
  const toggleMode = () => {
    setFade(false);
    setTimeout(() => {
      setIsLogin((prev) => !prev);
      setError('');
      setFade(true);
    }, 300);
  };

  return (
    <div className="auth-page">
      {/* Left Side */}
      <div className="left-side">
        <div className="left-content">
          <h1>Welcome</h1>
          <p>Manage your inventory with ease.</p>
        </div>
      </div>

      {/* Right Side */}
      <div className="right-side">
        <Card className={`auth-card ${!isLogin ? 'register-mode' : ''}`}>
          <Card.Body className={fade ? 'fade-in' : 'fade-out'}>
            <h4>{isLogin ? 'Login in' : 'Register'}</h4>
            {error && <Alert variant="danger">{error}</Alert>}
            <Form onSubmit={handleSubmit}>
              {/* In registration mode, role dropdown at the top */}
              {!isLogin && (
                <Form.Group className="mb-3" controlId="formRole">
                  <Form.Label className="form-label">Role</Form.Label>
                  <Form.Select
                    className="form-control role-select"
                    value={role}
                    onChange={(e) => setRole(e.target.value)}
                    required
                  >
                    <option value="" disabled>Select</option>
                    <option value="supplier">Supplier</option>
                    <option value="owner">Owner</option>
                    <option value="employee">Employee</option>
                  </Form.Select>
                </Form.Group>
              )}
              <Form.Group className="mb-3" controlId="formUsername">
                <Form.Label className="form-label">Username</Form.Label>
                <Form.Control
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </Form.Group>
              <Form.Group className="mb-3" controlId="formPassword">
                <Form.Label className="form-label">Password</Form.Label>
                <Form.Control
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </Form.Group>
              <Button variant="primary" type="submit" className="mb-3">
                {isLogin ? 'Login in' : 'Register'}
              </Button>
            </Form>
            <div className="divider">
              <span>{isLogin ? 'or login in with' : 'or register with'}</span>
            </div>
            <div className="social-buttons">
              <div className="social-button">
                <Button variant="outline-info" className="google-btn" onClick={googleLogin}>
                  <img src="https://developers.google.com/identity/images/g-logo.png" alt="Google" />
                  {isLogin ? 'Login in with Google' : 'Register with Google'}
                </Button>
              </div>
              <div className="social-button">
                <LinkedIn
                  clientId="86bco2ofgo5q6u"
                  redirectUri={`${window.location.origin}/linkedin`}
                  onSuccess={(code) => handleSocialLogin('linkedin', code)}
                  onError={() => {}}
                  scope="openid profile email"
                >
                  {({ linkedInLogin, disabled }) => (
                    <Button
                      variant="outline-info"
                      className="linkedin-btn"
                      onClick={() => {
                        if (typeof linkedInLogin === 'function') linkedInLogin();
                      }}
                      disabled={disabled}
                    >
                      <img src="https://cdn-icons-png.flaticon.com/512/174/174857.png" alt="LinkedIn" />
                      {isLogin ? 'Login in with LinkedIn' : 'Register with LinkedIn'}
                    </Button>
                  )}
                </LinkedIn>
              </div>
            </div>
            <div className="text-center mt-3 account-switch">
              {isLogin ? (
                <>
                  Donâ€™t have an account?{' '}
                  <span
                    onClick={toggleMode}
                    style={{
                      cursor: 'pointer',
                      color: '#007bff',
                      textDecoration: 'underline',
                      background: 'none',
                      padding: 0,
                      border: 'none'
                    }}
                  >
                    Register here
                  </span>
                </>
              ) : (
                <>
                  Already have an account?{' '}
                  <span
                    onClick={toggleMode}
                    style={{
                      cursor: 'pointer',
                      color: '#007bff',
                      textDecoration: 'underline',
                      background: 'none',
                      padding: 0,
                      border: 'none'
                    }}
                  >
                    Login in here
                  </span>
                </>
              )}
            </div>
          </Card.Body>
        </Card>
      </div>
    </div>
  );
}

export default AuthPage;
