// src/components/AuthLogin_Register/AuthPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Form, Button, Alert } from 'react-bootstrap';
import { GoogleLogin } from '@react-oauth/google';
import { LinkedIn } from 'react-linkedin-login-oauth2';
import { FaGoogle } from 'react-icons/fa';
import './AuthPage.css';

function AuthPage({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [error, setError] = useState('');

  // Common fields
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // Registration-only fields
  const [contactType, setContactType] = useState('email'); // 'email' or 'phone'
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [role, setRole] = useState('owner');

  // Optional debug logging for layout
  useEffect(() => {
    const logStyles = () => {
      const authPage = document.querySelector('.auth-page');
      if (authPage) {
        const computedStyles = window.getComputedStyle(authPage);
        console.log("AuthPage computed styles:", {
          height: computedStyles.height,
          margin: computedStyles.margin,
          padding: computedStyles.padding,
          display: computedStyles.display,
        });
        console.log("AuthPage clientHeight:", authPage.clientHeight, "Window innerHeight:", window.innerHeight);
        console.log("AuthPage bounding rect:", authPage.getBoundingClientRect());
      }
    };
    logStyles();
    window.addEventListener('resize', logStyles);
    return () => window.removeEventListener('resize', logStyles);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        const res = await axios.post('http://localhost:5001/api/auth/login', { username, password });
        const token = res.data.accessToken;
        onLogin(token);
      } else {
        const registrationData = { username, password, role };
        if (contactType === 'email') {
          registrationData.email = email;
        } else {
          registrationData.phone = phone;
        }
        await axios.post('http://localhost:5001/api/auth/signup', registrationData);
        // Switch to login mode after successful registration
        setIsLogin(true);
        setError('');
        setUsername('');
        setPassword('');
        setEmail('');
        setPhone('');
        setRole('owner');
      }
    } catch (err) {
      setError(
        err.response?.data?.error ||
          (isLogin
            ? 'Login failed. Please check your credentials.'
            : 'Registration failed. Please try again.')
      );
    }
  };

  const handleSocialLogin = async (provider, response) => {
    // Replace this stub with your actual OAuth flow.
    const dummyToken = `dummy-${provider}-token`;
    onLogin(dummyToken);
  };

  // Shared inline style for both social login buttons.
  const socialBtnStyle = {
    width: '100%',
    borderRadius: '6px',
    backgroundColor: '#fff',
    padding: '0.75rem',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  };

  return (
    <div className="auth-page">
      {/* Left Side */}
      <div className="left-side">
        <div className="left-content">
          <h1>Welcome</h1>
          <p>Manage your inventory with ease.</p>
        </div>
      </div>

      {/* Right Side */}
      <div className="right-side">
        <Card className="auth-card">
          <Card.Body>
            <h4>{isLogin ? 'Login' : 'Register'}</h4>
            {error && <Alert variant="danger">{error}</Alert>}
            <Form onSubmit={handleSubmit}>
              <Form.Group className="mb-3" controlId="formUsername">
                <Form.Label className="form-label">Username</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter your username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </Form.Group>
              <Form.Group className="mb-3" controlId="formPassword">
                <Form.Label className="form-label">Password</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Enter your password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </Form.Group>
              {!isLogin && (
                <>
                  <Form.Group className="mb-3" controlId="contactTypeToggle">
                    <Form.Label className="form-label">Register With</Form.Label>
                    <div>
                      <Button
                        variant={contactType === 'email' ? 'primary' : 'outline-primary'}
                        onClick={() => setContactType('email')}
                        className="me-2"
                      >
                        Email
                      </Button>
                      <Button
                        variant={contactType === 'phone' ? 'primary' : 'outline-primary'}
                        onClick={() => setContactType('phone')}
                      >
                        Phone
                      </Button>
                    </div>
                  </Form.Group>
                  {contactType === 'email' ? (
                    <Form.Group className="mb-3" controlId="formEmail">
                      <Form.Label className="form-label">Email</Form.Label>
                      <Form.Control
                        type="email"
                        placeholder="Enter your email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                      />
                    </Form.Group>
                  ) : (
                    <Form.Group className="mb-3" controlId="formPhone">
                      <Form.Label className="form-label">Phone Number</Form.Label>
                      <Form.Control
                        type="tel"
                        placeholder="Enter your phone number"
                        value={phone}
                        onChange={(e) => setPhone(e.target.value)}
                        required
                      />
                    </Form.Group>
                  )}
                  <Form.Group className="mb-3" controlId="formRole">
                    <Form.Label className="form-label">Role</Form.Label>
                    <Form.Select value={role} onChange={(e) => setRole(e.target.value)}>
                      <option value="owner">Owner</option>
                      <option value="supplier">Supplier</option>
                    </Form.Select>
                  </Form.Group>
                </>
              )}
              <Button variant="primary" type="submit" className="mb-3" style={{ width: '100%', borderRadius: '6px' }}>
                {isLogin ? 'Login' : 'Register'}
              </Button>
            </Form>
            {/* Divider with centered text */}
            <div className="divider">
              <span>or login with</span>
            </div>
            {/* Combined social login buttons */}
            <div className="social-buttons">
              <div className="social-button">
                <GoogleLogin
                  render={({ onClick, disabled }) => (
                    <Button
                      variant="outline-info"
                      onClick={onClick}
                      disabled={disabled}
                      style={socialBtnStyle}
                    >
                      <FaGoogle style={{ marginRight: '8px' }} />
                      Login with Google
                    </Button>
                  )}
                  onSuccess={(credentialResponse) => handleSocialLogin('google', credentialResponse)}
                  onError={() => {}}
                />
              </div>
              <div className="social-button">
                <LinkedIn
                  clientId="86bco2ofgo5q6u"
                  redirectUri={`${window.location.origin}/linkedin`}
                  onSuccess={(code) => handleSocialLogin('linkedin', code)}
                  onError={() => {}}
                  scope="openid profile email"
                >
                  {({ linkedInLogin, disabled }) => (
                    <Button
                      variant="outline-info"
                      onClick={() => {
                        if (typeof linkedInLogin === 'function') linkedInLogin();
                      }}
                      disabled={disabled}
                      style={socialBtnStyle}
                    >
                      <img
                        src="https://cdn-icons-png.flaticon.com/512/174/174857.png"
                        alt="LinkedIn"
                        style={{
                          marginRight: '8px',
                          marginLeft: '-0.75rem', // This shifts the logo to the very left edge
                          width: '20px',
                          height: '20px'
                        }}
                      />
                      Login with LinkedIn
                    </Button>
                  )}
                </LinkedIn>
              </div>
            </div>
            <div className="text-center mt-3">
              {isLogin ? (
                <>
                  Donâ€™t have an account?{' '}
                  <Button variant="link" onClick={() => { setIsLogin(false); setError(''); }}>
                    Register here
                  </Button>
                </>
              ) : (
                <>
                  Already have an account?{' '}
                  <Button variant="link" onClick={() => { setIsLogin(true); setError(''); }}>
                    Login here
                  </Button>
                </>
              )}
            </div>
          </Card.Body>
        </Card>
