// frontend/src/components/AuthPage.js
import React, { useState } from 'react';
import axios from 'axios';
import { Container, Row, Col, Card, Form, Button, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import './AuthPage.css'; // Custom CSS for full-page design

function AuthPage({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true); 
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Common form fields
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // Only needed if registering
  const [role, setRole] = useState('owner'); // or 'supplier'

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        // LOGIN mode
        const res = await axios.post('http://localhost:5001/api/auth/login', {
          username,
          password,
        });
        const token = res.data.accessToken;
        onLogin(token);
      } else {
        // REGISTER mode
        await axios.post('http://localhost:5001/api/auth/signup', {
          username,
          password,
          role,
        });
        // After successful signup, either auto-login or go to login mode:
        setIsLogin(true);
        setError('');
        setUsername('');
        setPassword('');
        setRole('owner');
        // Optionally navigate right away:
        // navigate('/');
      }
    } catch (err) {
      setError(
        err.response?.data?.error ||
          (isLogin
            ? 'Login failed. Please check your credentials.'
            : 'Registration failed. Please try again.')
      );
    }
  };

  return (
    <div className="auth-page-bg">
      <Container fluid className="auth-page-container">
        <Row className="justify-content-center align-items-center min-vh-100">
          <Col md={6} lg={4}>
            <Card className="p-4 shadow-lg">
              <Card.Body>
                <h2 className="text-center mb-4">Smart Inventory</h2>
                <h4 className="text-center mb-4">
                  {isLogin ? 'Login' : 'Register'}
                </h4>

                {error && <Alert variant="danger">{error}</Alert>}

                <Form onSubmit={handleSubmit}>
                  <Form.Group className="mb-3" controlId="formUsername">
                    <Form.Label>Username</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Enter username"
                      value={username}
                      onChange={(e) => setUsername(e.target.value)}
                      required
                    />
                  </Form.Group>

                  <Form.Group className="mb-3" controlId="formPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                      type="password"
                      placeholder="Enter password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      required
                    />
                  </Form.Group>

                  {!isLogin && (
                    <Form.Group className="mb-3" controlId="formRole">
                      <Form.Label>Role</Form.Label>
                      <Form.Select
                        value={role}
                        onChange={(e) => setRole(e.target.value)}
                      >
                        <option value="owner">Owner</option>
                        <option value="supplier">Supplier</option>
                      </Form.Select>
                    </Form.Group>
                  )}

                  <Button variant="success" type="submit" className="w-100 mb-3">
                    {isLogin ? 'Login' : 'Register'}
                  </Button>
                </Form>

                <div className="text-center">
                  {isLogin ? (
                    <>
                      Don’t have an account?{' '}
                      <Button
                        variant="link"
                        onClick={() => {
                          setIsLogin(false);
                          setError('');
                        }}
                      >
                        Register here
                      </Button>
                    </>
                  ) : (
                    <>
                      Already have an account?{' '}
                      <Button
                        variant="link"
                        onClick={() => {
                          setIsLogin(true);
                          setError('');
                        }}
                      >
                        Login here
                      </Button>
                    </>
                  )}
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default AuthPage;
