// frontend/src/components/SignupPage.js
import React, { useState } from 'react';
import axios from 'axios';
import { Container, Row, Col, Form, Button, Alert } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';

function SignupPage() {
  const [form, setForm] = useState({ username: '', password: '', role: 'owner' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Send the signup request to the backend
      const res = await axios.post('http://localhost:5001/api/auth/signup', form);
      // After successful signup, redirect to the login page or sign the user in
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.error || 'Signup failed. Please try again.');
    }
  };

  return (
    <Container className="mt-5">
      <Row className="justify-content-md-center">
        <Col md={6}>
          <h2 className="text-center">Sign Up</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="username" className="mb-3">
              <Form.Label>Username</Form.Label>
              <Form.Control 
                type="text" 
                placeholder="Enter username" 
                value={form.username}
                onChange={(e) => setForm({ ...form, username: e.target.value })}
                required 
              />
            </Form.Group>
            <Form.Group controlId="password" className="mb-3">
              <Form.Label>Password</Form.Label>
              <Form.Control 
                type="password" 
                placeholder="Enter password" 
                value={form.password}
                onChange={(e) => setForm({ ...form, password: e.target.value })}
                required 
              />
            </Form.Group>
            <Form.Group controlId="role" className="mb-3">
              <Form.Label>Role</Form.Label>
              <Form.Select
                value={form.role}
                onChange={(e) => setForm({ ...form, role: e.target.value })}
              >
                <option value="owner">Owner</option>
                <option value="supplier">Supplier</option>
              </Form.Select>
            </Form.Group>
            <Button variant="primary" type="submit" className="w-100">
              Sign Up
            </Button>
          </Form>
          <div className="text-center mt-3">
            Already have an account? <Link to="/">Login here</Link>
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default SignupPage;
