// src/components/AuthLogin_Register/AuthPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Row, Col, Card, Form, Button, Alert } from 'react-bootstrap';
import { GoogleLogin } from '@react-oauth/google';
import { LinkedIn } from 'react-linkedin-login-oauth2';
import { FaGoogle, FaLinkedinIn } from 'react-icons/fa';
import './AuthPage.css';

function AuthPage({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [error, setError] = useState('');

  // Common fields
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // Registration-only fields
  const [contactType, setContactType] = useState('email'); // 'email' or 'phone'
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [role, setRole] = useState('owner');

  useEffect(() => {
    const logStyles = () => {
      const authPage = document.querySelector('.auth-page');
      if (authPage) {
        const computedStyles = window.getComputedStyle(authPage);
        console.log("AuthPage computed styles:", {
          height: computedStyles.height,
          margin: computedStyles.margin,
          padding: computedStyles.padding,
          display: computedStyles.display,
        });
        console.log("AuthPage clientHeight:", authPage.clientHeight, "Window innerHeight:", window.innerHeight);
        console.log("AuthPage bounding rect:", authPage.getBoundingClientRect());
      } else {
        console.log("AuthPage container not found in the DOM.");
      }
      // Log details for the body element
      const bodyStyles = window.getComputedStyle(document.body);
      console.log("Body computed styles:", {
        height: bodyStyles.height,
        margin: bodyStyles.margin,
        padding: bodyStyles.padding,
      });
      console.log("document.body.clientHeight:", document.body.clientHeight);
      console.log("document.documentElement.clientHeight:", document.documentElement.clientHeight);
      // Log window dimensions and scroll offsets
      console.log("Window innerWidth:", window.innerWidth, "Window scrollX:", window.scrollX, "scrollY:", window.scrollY);
    };

    // Log initially on mount
    logStyles();
    // Also log on window resize to catch dynamic changes
    window.addEventListener('resize', logStyles);
    return () => window.removeEventListener('resize', logStyles);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        const res = await axios.post('http://localhost:5001/api/auth/login', { username, password });
        const token = res.data.accessToken;
        onLogin(token);
      } else {
        const registrationData = { username, password, role };
        if (contactType === 'email') {
          registrationData.email = email;
        } else {
          registrationData.phone = phone;
        }
        await axios.post('http://localhost:5001/api/auth/signup', registrationData);
        setIsLogin(true);
        setError('');
        setUsername('');
        setPassword('');
        setEmail('');
        setPhone('');
        setRole('owner');
      }
    } catch (err) {
      setError(
        err.response?.data?.error ||
          (isLogin
            ? 'Login failed. Please check your credentials.'
            : 'Registration failed. Please try again.')
      );
    }
  };

  const handleSocialLogin = async (provider, response) => {
    // TODO: Replace this stub with your actual OAuth flow.
    const dummyToken = `dummy-${provider}-token`;
    onLogin(dummyToken);
  };

  return (
    <div className="auth-page">
      <Container fluid>
        <Row className="min-vh-100">
          {/* Left Side: Green background & Text */}
          <Col md={6} className="left-side d-flex align-items-center justify-content-center">
            <div className="left-content text-center">
              <h1>Welcome to Smart Inventory</h1>
              <p>Efficiently manage your stock, orders, and suppliers with ease.</p>
            </div>
          </Col>
          {/* Right Side: Form Card */}
          <Col xs={12} md={6} className="right-side d-flex align-items-center justify-content-center">
            <Card className={`auth-card shadow ${!isLogin ? 'wider-card' : ''}`}>
              <Card.Body>
                <h2 className="text-center mb-3">Smart Inventory</h2>
                <h4 className="text-center mb-4">{isLogin ? 'Login' : 'Register'}</h4>
                {error && <Alert variant="danger">{error}</Alert>}
                <Form onSubmit={handleSubmit}>
                  <Form.Group className="mb-3" controlId="formUsername">
                    <Form.Label>Username</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Enter username"
                      value={username}
                      onChange={(e) => setUsername(e.target.value)}
                      required
                    />
                  </Form.Group>
                  {!isLogin && (
                    <>
                      <Form.Group className="mb-3" controlId="contactTypeToggle">
                        <Form.Label>Register With</Form.Label>
                        <div>
                          <Button 
                            variant={contactType === 'email' ? 'primary' : 'outline-primary'}
                            onClick={() => setContactType('email')}
                            className="me-2"
                          >
                            Email
                          </Button>
                          <Button 
                            variant={contactType === 'phone' ? 'primary' : 'outline-primary'}
                            onClick={() => setContactType('phone')}
                          >
                            Phone
                          </Button>
                        </div>
                      </Form.Group>
                      {contactType === 'email' ? (
                        <Form.Group className="mb-3" controlId="formEmail">
                          <Form.Label>Email</Form.Label>
                          <Form.Control
                            type="email"
                            placeholder="Enter email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                          />
                        </Form.Group>
                      ) : (
                        <Form.Group className="mb-3" controlId="formPhone">
                          <Form.Label>Phone Number</Form.Label>
                          <Form.Control
                            type="tel"
                            placeholder="Enter phone number"
                            value={phone}
                            onChange={(e) => setPhone(e.target.value)}
                            required
                          />
                        </Form.Group>
                      )}
                    </>
                  )}
                  <Form.Group className="mb-3" controlId="formPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                      type="password"
                      placeholder="Enter password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      required
                    />
                  </Form.Group>
                  {!isLogin && (
                    <Form.Group className="mb-3" controlId="formRole">
                      <Form.Label>Role</Form.Label>
                      <Form.Select value={role} onChange={(e) => setRole(e.target.value)}>
                        <option value="owner">Owner</option>
                        <option value="supplier">Supplier</option>
                      </Form.Select>
                    </Form.Group>
                  )}
                  <Button variant="primary" type="submit" className="w-100 mb-3">
                    {isLogin ? 'Login' : 'Register'}
                  </Button>
                </Form>
                <div className="social-login text-center my-3">
                  <p>Or continue with</p>
                  <div className="social-buttons d-flex justify-content-center gap-3">
                    <div className="social-button">
                      <FaGoogle size={24} style={{ marginRight: '8px' }} />
                      <GoogleLogin
                        onSuccess={(credentialResponse) => handleSocialLogin('google', credentialResponse)}
                        onError={() => {}}
                      />
                    </div>
                    <div className="social-button">
                      <LinkedIn
                        clientId="86bco2ofgo5q6u"
                        redirectUri={`${window.location.origin}/linkedin`}
                        onSuccess={(code) => handleSocialLogin('linkedin', code)}
                        onError={() => {}}
                        scope="openid profile email"
                      >
                        {({ linkedInLogin, disabled }) => (
                          <Button
                            variant="outline-info"
                            onClick={() => {
                              if (typeof linkedInLogin === 'function') linkedInLogin();
                            }}
                            disabled={disabled}
                          >
                            <FaLinkedinIn />
                          </Button>
                        )}
                      </LinkedIn>
                    </div>
                  </div>
                </div>
                <div className="text-center mt-3">
                  {isLogin ? (
                    <>
                      Don’t have an account?{' '}
                      <Button variant="link" onClick={() => { setIsLogin(false); setError(''); }}>
                        Register here
                      </Button>
                    </>
                  ) : (
                    <>
                      Already have an account?{' '}
                      <Button variant="link" onClick={() => { setIsLogin(true); setError(''); }}>
                        Login here
                      </Button>
                    </>
                  )}
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default AuthPage;
