// src/components/AuthLogin_Register/AuthPage.js
import React, { useState } from 'react';
import axios from 'axios';
import { Card, Form, Button, Modal } from 'react-bootstrap';
import { useGoogleLogin } from '@react-oauth/google';
import { LinkedIn } from 'react-linkedin-login-oauth2';
import './AuthPage.css';

// CustomSelect component for Role dropdown remains the same
function CustomSelect({ value, onChange, options, placeholder }) {
  const [open, setOpen] = useState(false);

  const handleSelect = (val) => {
    onChange(val);
    setOpen(false);
  };

  return (
    <div className="select-menu">
      <div className="select-btn" onClick={() => setOpen(!open)}>
        <span className="sBtn-text">
          {value ? options.find((o) => o.value === value)?.label : placeholder}
        </span>
        <i className="bx bx-chevron-down"></i>
      </div>
      {open && (
        <ul className="options">
          {options.map((option) => (
            <li
              className="option"
              key={option.value}
              onClick={() => handleSelect(option.value)}
            >
              <span className="option-text">{option.label}</span>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

function AuthPage({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [fade, setFade] = useState(true);
  // Instead of a combined message state, we will show messages in a modal
  const [message, setMessage] = useState({ type: '', text: '' });
  const [showModal, setShowModal] = useState(false);

  // Common fields
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // Registration-only fields
  const [email, setEmail] = useState('');
  const [role, setRole] = useState('');

  const roleOptions = [
    { value: 'supplier', label: 'Supplier' },
    { value: 'owner', label: 'Owner' },
    { value: 'employee', label: 'Employee' }
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Ensure role is selected when registering
    if (!isLogin && !role) {
      showMessage('error', 'Role is required for registration.');
      return;
    }

    try {
      if (isLogin) {
        // Send the username and password exactly as entered (preserving case)
        const res = await axios.post('http://localhost:5001/api/auth/login', { username, password });
        const token = res.data.accessToken;
        onLogin(token);
      } else {
        // Registration data is sent without any case transformation
        const registrationData = { username, password, email, role };
        await axios.post('http://localhost:5001/api/auth/signup', registrationData);
        showMessage('success', 'Registration successful! Please log in.');
        // After a delay, switch back to login mode and clear all fields
        setTimeout(() => {
          toggleMode();
        }, 2000);
      }
    } catch (err) {
      showMessage(
        'error',
        err.response?.data?.error ||
          (isLogin
            ? 'Login failed. Please check your credentials.'
            : 'Registration failed. Please try again.')
      );
    }
  };

  // Function to update message and show the modal
  const showMessage = (type, text) => {
    setMessage({ type, text });
    setShowModal(true);
  };

  // Handle modal close
  const handleModalClose = () => {
    setShowModal(false);
    setMessage({ type: '', text: '' });
  };

  // Toggle mode with a fade transition and reset all form fields
  const toggleMode = () => {
    setFade(false);
    setTimeout(() => {
      setIsLogin((prev) => !prev);
      // Reset all form fields when switching modes
      setUsername('');
      setPassword('');
      setEmail('');
      setRole('');
      setFade(true);
    }, 300);
  };

  const handleSocialLogin = async (provider, response) => {
    const dummyToken = `dummy-${provider}-token`;
    onLogin(dummyToken);
  };

  const googleLogin = useGoogleLogin({
    onSuccess: (credentialResponse) => handleSocialLogin('google', credentialResponse),
    onError: () => {},
  });

  return (
    <div className="auth-page">
      {/* Left Side */}
      <div className="left-side">
        <div className="left-content">
          <h1>Welcome</h1>
          <p>Manage your inventory with ease.</p>
        </div>
      </div>

      {/* Right Side */}
      <div className="right-side">
        <Card className={`auth-card ${!isLogin ? 'register-mode' : ''}`}>
          <Card.Body className={fade ? 'fade-in' : 'fade-out'}>
            <h4>{isLogin ? 'Login' : 'Register'}</h4>
            <Form onSubmit={handleSubmit}>
              {!isLogin && (
                <Form.Group className="mb-3" controlId="formRole">
                  <Form.Label className="form-label">Role</Form.Label>
                  <CustomSelect
                    value={role}
                    onChange={setRole}
                    options={roleOptions}
                    placeholder="Select your role"
                  />
                </Form.Group>
              )}
              <Form.Group className="mb-3" controlId="formUsername">
                <Form.Label className="form-label">Username</Form.Label>
                <Form.Control
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </Form.Group>
              {!isLogin && (
                <Form.Group className="mb-3" controlId="formEmail">
                  <Form.Label className="form-label">Email</Form.Label>
                  <Form.Control
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </Form.Group>
              )}
              <Form.Group className="mb-3" controlId="formPassword">
                <Form.Label className="form-label">Password</Form.Label>
                <Form.Control
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </Form.Group>
              <Button variant="primary" type="submit" className="mb-3">
                {isLogin ? 'Login' : 'Register'}
              </Button>
            </Form>
            <div className="divider">
              <span>{isLogin ? 'or login with' : 'or register with'}</span>
            </div>
            <div className="social-buttons">
              <div className="social-button">
                <Button variant="outline-info" className="google-btn" onClick={googleLogin}>
                  <img src="https://developers.google.com/identity/images/g-logo.png" alt="Google" />
                  {isLogin ? 'Login with Google' : 'Register with Google'}
                </Button>
              </div>
              <div className="social-button">
                <LinkedIn
                  clientId="86bco2ofgo5q6u"
                  redirectUri={`${window.location.origin}/linkedin`}
                  onSuccess={(code) => handleSocialLogin('linkedin', code)}
                  onError={() => {}}
                  scope="openid profile email"
                >
                  {({ linkedInLogin, disabled }) => (
                    <Button
                      variant="outline-info"
                      className="linkedin-btn"
                      onClick={() => {
                        if (typeof linkedInLogin === 'function') linkedInLogin();
                      }}
                      disabled={disabled}
                    >
                      <img src="https://cdn-icons-png.flaticon.com/512/174/174857.png" alt="LinkedIn" />
                      {isLogin ? 'Login with LinkedIn' : 'Register with LinkedIn'}
                    </Button>
                  )}
                </LinkedIn>
              </div>
            </div>
            <div className="text-center mt-3 account-switch">
              {isLogin ? (
                <>
                  Don’t have an account?{' '}
                  <span
                    onClick={toggleMode}
                    style={{
                      cursor: 'pointer',
                      color: '#007bff',
                      textDecoration: 'underline',
                      background: 'none',
                      padding: 0,
                      border: 'none'
                    }}
                  >
                    Register here
                  </span>
                </>
              ) : (
                <>
                  Already have an account?{' '}
                  <span
                    onClick={toggleMode}
                    style={{
                      cursor: 'pointer',
                      color: '#007bff',
                      textDecoration: 'underline',
                      background: 'none',
                      padding: 0,
                      border: 'none'
                    }}
                  >
                    Login here
                  </span>
                </>
              )}
            </div>
          </Card.Body>
        </Card>
      </div>

      {/* Bootstrap Modal for notifications */}
      <Modal show={showModal} onHide={handleModalClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>{message.type === 'error' ? 'Error' : 'Success'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>{message.text}</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleModalClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default AuthPage;
