import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function LinkedInCallback({ onLogin }) {
  const location = useLocation();
  const navigate = useNavigate();
  const [errorMsg, setErrorMsg] = useState(null);

  useEffect(() => {
    console.log('LinkedIn callback received. Location:', location);
    const query = new URLSearchParams(location.search);
    const code = query.get('code');
    const error = query.get('error');

    if (error) {
      console.error('LinkedIn callback error:', error);
      setErrorMsg(error);
      // Optionally, redirect after 5 seconds
      setTimeout(() => {
         navigate('/');
      }, 5000);
      return;
    }
    if (code) {
      console.log('LinkedIn callback code:', code);
      // Normally, exchange the code for an access token here.
      onLogin(`dummy-linkedin-token-${code}`);
      navigate('/');
    }
  }, [location, navigate, onLogin]);

  return (
    <div>
      {errorMsg ? (
        <div>
          <p>Bummer, something went wrong: {errorMsg}</p>
          <p>You will be redirected shortly...</p>
        </div>
      ) : (
        <p>Processing LinkedIn login...</p>
      )}
    </div>
  );
}

export default LinkedInCallback;
